mapper.sql:
where 1=1 and deleted = 0


		and id = #{id}


		and name like CONCAT('%', #{name}, '%')


		and url = #{url}



		and parentId = #{parentId}



		and parentId is null



		and createTime = #{createTime}


		and rank = #{rank}


		and actions = #{actions}

mapper.update:
update sys_menu set
id=#{id},name=#{name},url=#{url},parentId=#{parentId},deleted=#{deleted},updateTime
= now(),rank=#{rank},actions=#{actions} where id=#{id}

mapper.select:
select

,(SELECT COUNT(*) FROM sys_menu c WHERE c.parentId = m.id and deleted
= 0) AS subCount
from sys_menu m


	${pager.orderCondition}


	${pager.mysqlQueryCondition}

select id,name,url,parentId,actions,rank,date_format(createTime, '%Y-%m-%d %H:%i:%s') createTime,date_format(updateTime, '%Y-%m-%d %H:%i:%s') updateTime from sys_menu where deleted= 0 and parentId is null
and id != #{menuId}
order by rank

select id,name,url,parentId,actions,rank,date_format(createTime, '%Y-%m-%d %H:%i:%s') createTime,date_format(updateTime, '%Y-%m-%d %H:%i:%s') from sys_menu where deleted= 0 and parentId is not null
and parentId = #{parentId}
order by rank

SELECT id,NAME,url,parentId,actions FROM sys_menu m WHERE deleted= 0 AND
parentId IS NOT NULL
AND EXISTS (
SELECT * FROM sys_role_rel r WHERE r.objId = m.id AND (relType = 0 or relType = 2)
AND r.roleId = #{roleId}
)

SELECT DISTINCT id,NAME,url,parentId,actions FROM sys_menu m
WHERE deleted= 0 AND parentId IS NULL
AND EXISTS (
SELECT * FROM sys_role_rel r
WHERE r.objId = m.id AND (r.relType = 0 or r.relType = 2)
AND EXISTS (
SELECT 1 FROM sys_role_rel u WHERE u.roleId = r.roleId AND u.relType = 1 AND
u.objId = #{userId}
)
)

SELECT DISTINCT id,NAME,url,parentId,actions FROM sys_menu m
WHERE deleted= 0 AND parentId IS NOT NULL
AND EXISTS (
SELECT * FROM sys_role_rel r
WHERE r.objId = m.id AND (r.relType = 0 or r.relType = 2)
AND EXISTS (
SELECT 1 FROM sys_role_rel u WHERE u.roleId = r.roleId AND u.relType = 1 AND
u.objId = #{userId}
)
)

select t1.id,t1.name,t1.url,t1.parentId,t1.deleted,t1.createTime,t1.updateTime,t1.rank,t1.actions from sys_menu t1 where 1=1 and t1.id in ( select t3.objId from sys_role_rel t3 where (t3.relType=0 or t3.relType=2) and t3.roleId in ( select t2.roleId from sys_role_rel t2 where 1=1
and t2.relType=1 and t2.objId=#{userId}
) ) order by t1.parentId, t1.rank
